expira_en_1dia.script_on_login:
:if ([:len [/system scheduler find name="$user"]] = 0) do={
    /system scheduler add name="$user" start-time=[:totime ([:datetime] + 24h)] interval=0s on-event="
        /ip hotspot active remove [find where user=\\"$user\\"]
        /ip hotspot user remove [find where name=\\"$user\\"]
        /log info \\"Usuario $user eliminado después de 1 hora de activacion\\"
    "df
    /log info "Programada eliminacion del usuario $user en 1 hora"
} else={
    /log info "La tarea programada para el usuario $user ya existe"
}
